            var afSub1 = {keys:["utm_term"]};
            var afSub2 = {keys:["utm_medium"]};
            var ad = {keys:["utm_content"]};
            var googleClickIdKey = "af_sub3";
            var afSub5 = {keys:["fbclid"]};
            var from_alanbase = {paramKey:"from_alanbase",keys:["from_alanbase"],defaultValue:"0"};
            var afSub4 = {keys:["click_id"]};
            var custom_ss_ui = {paramKey:"af_ss_ui",defaultValue:"true"};
            var custom_ss_gtm_ui = {paramKey:"af_ss_gtm_ui",defaultValue:"true"};

            if (!window.AF_SMART_SCRIPT || !window.AF_SMART_SCRIPT.generateOneLinkURL) {
                return;
            }

            var result = window.AF_SMART_SCRIPT.generateOneLinkURL({
                oneLinkURL: oneLinkURL,
                afParameters: {
                    mediaSource: mediaSource,
                    campaign: campaign,
                    afSub1: afSub1,
                    afSub2: afSub2,
                    ad: ad,
                    googleClickIdKey: googleClickIdKey,
                    afSub5: afSub5,
                    afCustom: [
                        from_alanbase,
                        custom_ss_ui,
                        custom_ss_gtm_ui
                    ],
                    afSub4: afSub4
